    r := gin.Default()

    r.POST("/upload", func(c *gin.Context) {
        // 从请求中读取文件
        f, _ := c.FormFile("f1")
        // 保存文件
        c.SaveUploadedFile(f, "./" + f.Filename)
        // 给客户端返回上传成功的消息
        c.JSON(200, gin.H{"code": 2003, "msg": "上传成功"})
    })

