    }

    if u.Name == newName {
        u.sendMsg("改了个寂寞\n")
        return
    }

    u.server.mapLock.Lock()
    delete(u.server.onlineMap, u.Name)
    u.server.onlineMap[newName] = u
    u.server.mapLock.Unlock()

    u.Name = newName
    u.sendMsg("修改成功\n")
}
