    u.server.mapLock.Lock()
    userName := ""
    for _, user := range u.server.onlineMap {
        userName += "[ " + user.Name + " @ " + user.Addr + "]" + "\n"
    }
    u.server.mapLock.Unlock()
    u.SendMsg(userName)

}

