package main

import (
    "fmt"
    "io"
    "net"
    "strings"
    "sync"
    "time"
)

type Server struct {
    IP        string
    Port      int
    onlineMap map[string]*User // 在线用户表onlinemap
    mapLock   sync.RWMutex
    message   chan string // 消息广播的 channel
}

func NewServer(IP string, port int) *Server {
    return &amp;Server{
        IP:        IP,
        Port:      port,
        onlineMap: make(map[string]*User),
        message:   make(chan string),
    }
}

func (s *Server) Start() {
    address := fmt.Sprintf("%s:%d", s.IP, s.Port)
    // new
    listener, err := net.Listen("tcp", address)
    if err != nil {
        fmt.Println("Listen err:", err)
        return
    }
    // close
    defer listener.Close()

    fmt.Println("=&gt; Server is listening on :", address)

    // 启动监听消息生成器的 goroutine
    go s.broadcast()

    for {
        // Accept
        conn, err := listener.Accept()
        if err != nil {
            fmt.Println("Conn Err:", err)
        }
        // handle
        go s.handler(conn)
    }
}

func (s *Server) handler(conn net.Conn) {
    fmt.Print("Connection Successfuly.\n")
    conn.Write([]byte("Connection Successfuly.\n"))

    user := NewUser(conn, s)
    user.Online()

    // 接收客户端发送的消息
    go s.massTexting(conn, user)

    // 当前广播阻塞
    for {
        select {
        case &lt;-user.isAlive:
            // 当前用户是活跃的，应重置计时器
            // 不做任何事情，为了激活select{}，使计时器重置
        case &lt;-time.After(time.Second * 10):
            // 已经超时，强制踢下线
            user.SendMsg("超时未有动作，您已被强迫下线。")
            user.Offline()
            // 关闭连接
            conn.Close()
        }
    }
}

func (s *Server) massTexting(conn net.Conn, user *User) {
    buf := make([]byte, 4096)
    for {
        n, err := conn.Read(buf) // 从客户端读取输入
        if err != nil &amp;&amp; err != io.EOF {
            fmt.Println("Conn Read Err:", err)
            return
        }
        if n == 0 || err == io.EOF {
            user.Offline()
            conn.Close()
            return
        }
        // 提取用户的消息（去除\n）
        msg := string(buf[:n-1])
        args := strings.Split(msg, " ")
        // 针对msg 进行处理
        user.HandleMessage(args)

        user.isAlive &lt;- true
    }
}

// 消息生成器
func (s *Server) generateMsg(user *User, msg string) {
    sendMsg := "[" + user.Addr + "]" + user.Name + ": " + msg
    s.message &lt;- sendMsg
}

// 监听消息通道，然后进行播送
func (s *Server) broadcast() {
    for {
        msg := &lt;-s.message
        s.mapLock.Lock()
        for _, cli := range s.onlineMap {
            cli.C &lt;- msg
        }
        s.mapLock.Unlock()
    }
}
